Item.method_code:
// console.log("hc_hideNonEditableFields");

let isEditMode = parent.isEditMode

const classDisablingFormFields = "aras-form-field_disabled";
const classNameToRemove = "sys_f_container";
const disabledFields =  document.getElementsByClassName(classDisablingFormFields);

for (let i = 0; i<disabledFields.length; i++) {
    let disabledField = disabledFields[i];
    const name = disabledField.getAttribute("name");
    // console.log({name});
    if (isEditMode) {
        disabledField.hidden = true;
        disabledField.classList.remove(classNameToRemove);
    }
    else {
        disabledField.hidden = false;
        disabledField.classList.add(classNameToRemove);
    }
}
